    def parse_order_status(self, status):
        statuses = {
            '0': 'Start',
            '1': 'Partially Executed',  # partially filled
            '2': 'Executed',
            '3': 'Cancelled',
        }
        if status in statuses:
            return statuses[status]
        return status

